mapper.select:
  SELECT

  FROM(

  SELECT TOP ${size+from} ROW_NUMBER() OVER(

    order by ${sort} ${order}


    ORDER BY ctm_code DESC

) AS ROWID,*

  FROM customer



      make_op = #{condition.makeOpQx}


      and ctm_name like '%${condition.ctmName}%'


      and ctm_code = #{condition.ctmCode}


  ) AS TEMP1
  WHERE ROWID> ${from}

SELECT COUNT (*)
FROM customer


    make_op = #{condition.makeOpQx}


    and ctm_name like '%${condition.ctmName}%'


    and ctm_code = #{condition.ctmCode}
